INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (1, '"این روزها غربگرایان ایرانی حماس را بخاطر عملیات طوفان الاقصی و جمهوری اسلامی را بخاطر حمایت از حماس سرزنش می‌کنند. حماس را در کشتار و ویرانی که در غزه در جریان است مقصر می دانند و نکوهشش می کنند که چرا با وجود آگاهی از میزان درنده خویی و وحشی‌گری صهیونیست ها، این گونه متهورانه و بی‌محابا به سرزمین‌های غصب شده یورش برده است!؟
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (2, 'با ساخت ماه مصنوعی در دبی که یک پروژه عظیم و پر هزینه است، جمعیت بیشتری از قبل برای بازدید از آن راهی شهر دبی خواهند شد تا از نزدیک این پروژه بزرگ را ببینند و از امکانات موجود در آن بهره مند شوند. دبی یکی از شهرهای مدرن و در حال توسعه جهان است که هر روز با رونمایی از یک پروژه عظیم صنعت گردشگری خود را رونق می‌بخشد و سرمایه گذاران بیشتری ترغیب به اخذ اقامت دبی با خرید ملک  می‌شوند. پروژه‌ عظیم ماه مصنوعی در واقع یک مجتمع اقامتی است که توسط یک شرکت بزرگ کانادایی در حال ساخت است، این پروژه عظیم یکی از بهترین فرصت‌ها برای سرمایه گذاری در دبی هم محسوب می‌شود. البته برای خرید ملک از این پروژه بزرگ باید بودجه زیادی را کنار بگذارید! اما در شهر دبی فرصت‌های دیگری هم برای سرمایه گذاری روی ملک و دریافت اقامت این کشور وجود دارد که برای اطلاع از آنها می‌توانید با شرکت بزرگ روتس کپیتال یکی از بزرگترین مجموعه‌های فعال در حوزه ثبت شرکت و خرید ملک در دبی ارتباط بگیرید.
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (3, 'به گزارش الف؛ پایگاه خبری-تحلیلی "میدل ایست مانیتور" در گزارشی با اشاره به اوج گیری بحران "مهاجرت معکوس" شهروندان صهیونیست از اراضی اشغالی به خارج از این اراضی، به این نکته می پردازد که آمارهای مهاجرتی حاکی از این هستند که اکنون طیف های قابل ملاحظه ای از شهروندان صهیونیست، دیگر، اراضی اشغالی را مکانی امن برای خود جهت ادامه زندگی نمی بینند و به نحوی فعال به دنبال به دست آوردن پاسپورت یک کشور دیگر هستند. مساله ای که سبب شده تا صف های طولانی از شهروندان صهیونیست در برابر سفارتخانه‌های خارجی در تل آویو شکل بگیرد. معادله ای که با وقوع عملیات "طوفان اقصی" و آشکار شدن عینی بنیان های شکننده رژیم اشغالگر قدس، ابعاد جدی تری را نیز به خود گرفته است.
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (4, 'فرهیختگان نوشت: «چند هفته پیش نامه‌ای با موافقت چند تن از اعضای مجمع تشخیص نظام و به طرفیت حجت‌الاسلام و المسلمین اعرافی که خود ایشان نیز یکی از اعضای فقهی مجمع تشخیص هستند تنظیم شده و در رد راهکار بطلان معاملات قولنامه‌ای و بی‌اعتبار‌سازی این نوع از معاملات در محاکم برای دفتر رهبری ارسال شده است.
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (5, 'رهبر انقلاب اسلامی خاطرنشان کردند: همه در دنیای اسلام وظیفه دارند به مردم فلسطین کمک کنند.
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (6, 'به گزارش تابناک اقتصادی؛ بر اساس ماده ۴ قانون بهبود مستمر فضای کسب‌وکار، اتاق ایران باید شرایطی برای پایش و رصد محیط کسب‌وکار و اطلاع‌رسانی درباره وضعیت آن به مسئولان، سیاست‌گذاران و فعالان اقتصادی فراهم آورد؛ بنابراین، در سال ۹۷، پارلمان بخش خصوصی درصدد انتشار و محاسبه شاخص مدیران خرید (PMI) با برند شامخ برآمد.
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (7, 'بین الملل تابناک: جنگ میان گروه های مقاومت فلسطین علیه اسرائیل و بمباران وحشیانه غزه توسط رژیم اشغالگر قدس وارد هشتمین روز خود شده است. جنگی که بی سابقه ترین ضربه امنیتی، اطلاعاتی و نظامی را از سوی مقاومت فلسطین به اسرائیل وارد کرده و به نظر می رسد پیامدهای غیرقابل ترمیم زیادی را برای این رژیم به دنبال خواهد داشت. ', null, null, null, 'حملات موشکی حزب الله به 5 پایگاه نظامی رژیم صهیونیستی', '/post/img/7.jpg', 2, 2);
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (8, 'به گزارش تابناک به نقل از ایسنا، در هفته دوم لیگ قهرمانان آسیا سپاهان ایران در حالی در ورزشگاه نقش‌جهان اصفهان میزبان الاتحاد عربستان بود که این بازی به دلیل ترک ورزشگاه از سوی باشگاه عربستان لغو و با گزارش ناظر بازی پرونده آن به کمیته انضباطی AFC ارسال شد.
INSERT INTO persoanl_app_db.post (id, content, date, name, subtitle, title, img, coef, duration) VALUES (9, 'به گزارش تابناک به نقل از فارس، دبیرکل جبهه پایداری گفت: ما در جبهه پایداری به دنبال رونق بخشیدن بیشتر و بهتر به انتخابات هستیم، تا که مشارکت و حضور حداکثری مردم را در انتخابات داشته باشیم.
